import React, { useState } from "react";
import "../css/defaults.css";
let products = [
  { pName: "Product 1", pPrice: 100, pSku: 0 },
  { pName: "Product 2", pPrice: 100, pSku: 0 },
  { pName: "Product 3", pPrice: 100, pSku: 0 },
];


function ProductLoad() {
  // const addToProducts = [];
  for (let index = 0; index < products.length; index++) {
    const d = new Date();
    let day = d.getDate() + index;
    console.log(day);
    products[index].pSku = day;
    // products.push(addToProducts[index]);
  }
}
ProductLoad();
// On add THIS gets called
function ProductAdd() {
  products.push({pName: "Product 1", pPrice: 100, pSku: 0 })
  ProductLoad()
  
}

function ProductTemplate(props) {
  return (
    <>
      <div className="col-12 col-md-4 col-lg-3">
        <h2>{props.product.pName}</h2>
        <h2>{props.product.pPrice.toFixed(2)}$</h2>
        <h3>Sku: {props.product.pSku}</h3>
      </div>
    </>
  );
}

function ProductItem(props) {
  const products = props.products;
  const pListItems = products.map((product) => {
    return <ProductTemplate key={product.pSku} product={product} />;
  });
  return pListItems;
}

function CompProducts() {
  
  function update() {
    this.forceUpdate();
  }

  const [pNameInput, pNameInputSet] = useState("");
  return (
    <>
      <div className="custom-bg-3 min-vh-100 p-3">
        <div className="container min-vh-100 custom-bg-4 c-text-white text-center">
          <h2 className="display-3 fw-semibold">Products</h2>
          <hr />
          <div className="row justify-content-center">
            <ProductItem products={products} />
          </div>
          <div>
            <button onClick={ProductAdd}>add</button>
          </div>
        </div>
      </div>
    </>
  );
}

export default CompProducts;
