import '../css/CompPhotos.css'

const imagesSrc = document.querySelectorAll(".img-src");

const imageDisplay = document.getElementById("image-display");
const imageCaptionDisplay = document.getElementById("image-caption-display");

const nextBTN = document.getElementById("nextBTN");
const backBTN = document.getElementById("backBTN");

let currentSlide = 0;

// Slide Controls
function loadSlide() {
  imageDisplay.style.backgroundImage = `url(${imagesSrc[currentSlide].src})`;
  imageCaptionDisplay.innerText = imagesSrc[currentSlide].alt;
}

const timer = setInterval(() => {
  if (currentSlide >= imagesSrc.length - 1) {
    currentSlide = 0;
  } else {
    currentSlide++;
  }
  loadSlide();
}, 2500);

nextBTN.onclick = () => {
  clearInterval(timer)
  if (currentSlide >= imagesSrc.length - 1) {
    currentSlide = 0;
  } else {
    currentSlide++;
  }
  console.log(currentSlide);
  loadSlide();
};

backBTN.onclick = () => {
  clearInterval(timer)
  if (currentSlide == 0) {
    currentSlide = imagesSrc.length - 1;
  } else {
    currentSlide--;
  }
  loadSlide();
};

loadSlide();

function CompPhotos() {
  return (
    <div classname="min-vh-100">
      <div id="image-gallery" className="d-flex justify-content-center">
        {/* V IMG SRC LIST V */}
        <div id="image-display" className="d-flex">
          {/* Back BTN */}
          <button type="button" id="backBTN" className="BTN align-self-center">
            <span className="material-symbols-outlined image-arrow ">
              arrow_back
            </span>
          </button>
          {/* IMG Caption */}
          <p id="image-caption-display" className="align-self-end p-1  m-0">
            Caption
          </p>
          {/* Next BTN */}
          <button type="button" id="nextBTN" className="BTN align-self-center">
            <span className="material-symbols-outlined image-arrow">
              arrow_forward
            </span>
          </button>
        </div>
        {/* IMG SRC's */}
        <div id="img-src-list-container">
          {/* Src = Image Path / Alt = Caption */}
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-1.jpg"
            alt="1"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-2.jpg"
            alt="2"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-3.jpg"
            alt="3"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-4.jpg"
            alt="4"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-5.jpg"
            alt="5"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-6.jpg"
            alt="6"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-7.jpg"
            alt="7"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-8.jpg"
            alt="8"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-9.jpg"
            alt="9"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-10.jpg"
            alt="10"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-11.jpg"
            alt="Image 11"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-12.jpg"
            alt="Image 12"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-13.jpg"
            alt="Image 13"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-14.jpg"
            alt="Image 14"
          />
          <img
            className="img-src"
            src="../imgs/img-gal/img-gal-15.jpg"
            alt="Image 15"
          />
        </div>
      </div>
    </div>
  );
}

export default CompPhotos;
