import { useState, useEffect } from "react";
import CompAddProduct from "./CompAddProduct";

let products = [
  {
    pName: "Product 1",
    pPrice: 200,
    pDesc: `Test Desc`,
    pSku: 0,
    image: "test.png",
  },
  {
    pName: "Product 2",
    pPrice: 400,
    pDesc: `Test Desc`,
    pSku: 1,
    image: "test.png",
  },
  {
    pName: "Product 3",
    pPrice: 500,
    pDesc: `Test Desc`,
    pSku: 2,
    image: "test.png",
  },
];

function CompProducts() {
  const [allProducts, setAllProducts] = useState(null);
  //   function ProductAdd() {
  //     console.log("hi Mom");
  //     products.push({ pName: "Product 3", pPrice: 500, pSku: 4 });
  //   }

  const AddProduct = (newProduct) => {
    newProduct.pSku = allProducts.length + 1;
    const updatedProducts = [...allProducts, newProduct];
    setAllProducts(updatedProducts);
    // start
    fetch('/save-product', {
  method: 'POST',
  body: updatedProducts,
})
  .then((response) => response.json())
  .then((result) => {
    console.log('Success:', result);
  })
  .catch((error) => {
    console.error('Error:', error);
  });
    // end
    
  };

  function loadProducts() {
    fetch("/load-products")
      .then((response) => response.json())
      .then((data) => {
        setAllProducts(data);
      });
  }
useEffect(() => {
loadProducts()
}, []);


  return (
    <>
      <div className="custom-bg-3 min-vh-100 p-3">
        <div className="container min-vh-100 custom-bg-4 c-text-white text-center">
          <h2 className="display-3 fw-semibold">Products</h2>
          <hr />
          <CompAddProduct addProduct={AddProduct} />
          <hr />
          <div className="row justify-content-center p-1">
            {allProducts &&
              allProducts.map((product) => (
                <div
                  className="col-12 col-lg-3 custom-bg-5 m-1 p-2"
                  key={product.pSku}
                >
                  <img
                    src={"./upload-images/" + product.image}
                    alt=""
                    width={100}
                  />
                  <h2>{product.pName}</h2>
                  <h3>{product.pPrice}$</h3>
                  <p>{product.pDesc}</p>
                  <p>Sku: {product.pSku}</p>
                </div>
              ))}
          </div>
          {!allProducts && setAllProducts(products)}
          <div>{/* <button onClick={ProductAdd}>add</button> */}</div>
        </div>
        {/* <button
          onClick={() => {
            ProductAdd();
            setAllProducts(products);
          }}
        >
          Test
        </button> */}
      </div>
    </>
  );
}

export default CompProducts;
