import "../css/CompPhotos.css";
import React, { useState } from 'react';
function CompPhotos() {

    let [pageIndex, updatePageIndex] = useState(1);

    function pageNext() {
       updatePageIndex(pageIndex + 1)
    }
    function pagePrev() {
        updatePageIndex(pageIndex - 1)
     }

  return (
    <div className="min-vh-100 p-2">
      <div id="image-gallery" className="d-flex justify-content-center">
        {/* V IMG SRC LIST V */}
        <div id="image-display" className="d-flex">
          {/* Back BTN */}
          <button type="button" id="backBTN" className="BTN align-self-center">
            <span onClick={() => pagePrev()} className="material-symbols-outlined image-arrow ">
              arrow_back
            </span>
          </button>
          {/* IMG Caption */}
          <p id="image-caption-display" className="align-self-end p-1  m-0 text-center">
            {pageIndex}
          </p>
          {/* Next BTN */}
          <button type="button" id="nextBTN" className="BTN align-self-center">
            <span onClick={() => pageNext()} className="material-symbols-outlined image-arrow">
              arrow_forward
            </span>
          </button>
        </div>
        {/* IMG SRC's */}
        <div id="img-src-list-container">
          {/* Src = Image Path / Alt = Caption */}
          <img className="img-src" src="../../imgs/texture1.jpg" alt="1" />
        </div>
      </div>
    </div>
  );
}

export default CompPhotos;
